import React, { Component } from 'react';
import $ from 'jquery';

class AlbumsTable extends Component {

    constructor(){
        super();
        this.state = { albums:[] };
    }

    render(){
        return (
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>Artist</th>
                            <th>Description</th>
                            <th>Number of Songs</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.albums.map(function(album){
                                return (
                                    <tr key={album.id}>
                                        <td>{album.artist}</td>
                                        <td>{album.description}</td>
                                        <td>0</td>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                </table>
            </div>
        );
    }
}

export default AlbumsTable;
