import React, { Component } from 'react';
import $ from 'jquery';

class AlbumsTable extends Component {

    constructor(){
        super();
        this.state = { albums:[] };
    }

    componentDidMount(){
        $.ajax({
            url:"http://localhost:3001/albums",
            dataType: 'json',
            success:function(response){
                this.setState({albums:response});
            }.bind(this)
          }
        );
    }

    render(){
        return (
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>Artist</th>
                            <th>Description</th>
                            <th>Number of Songs</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {
                                this.state.albums.map(function(album){
                                    return (
                                        <tr>
                                            <td>{album.artist}</td>
                                            <td>{album.description}</td>
                                            <td>0</td>
                                        </tr>
                                    )
                                })
                            }  
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}

export default AlbumsTable;
